	if (! (i - l ==0)) return 0;
	if (! (j - 2*l == 0)) return 0;
	if (! (2*l - n >= 0)) return 0;
	if (! (2*l - n - 1 <= 0)) return 0;

	while (unknown_int())
	{
		if (unknown_int())
		{
			if (! (r -j  -1 >= 0)) return 0;
			i = j + 1;
			j = 2*j + 2;
		}
		else
		{
			if (unknown_int())
			{
				if (! (j -r <=0)) return 0;
				i = j;
				j = 2*j;
			}
			else
			{
				if (unknown_int())
				{
					if (! (l >=2)) return 0;
					if (! (r  >=2)) return 0;
					i = l - 1;
					j = 2 *l - 2;
					l = l - 1;
				}
				else
