  if(!cond) {abort();}
}

int main()
{
    int x=unknown_int();
    int z=unknown_int();
    if (!(x>-100)) return 0;
    if (!(x<200)) return 0;
    if (!(z>100)) return 0;
    if (!(z<200)) return 0;
    while(x<100 && z>100) 
    {
        _Bool tmp=unknown_bool();
        if (tmp) {
            x++;
        } else {
            x--;
            z--;
        }
    }                       

    {;//@ assert(x>=100 || z<=100);
}

    return 0;
}

```
