							Ma=0;

						}
						else
						{
							if (unknown_int())
							{
								if (! (Sa>=1)) return 0;
								I=I+Sa+Ea+Ma;
								Sa=0;
								Ea=1;
								Ma=0;
							}
							else
							{
								if (unknown_int())
								{
									if (! (Sa>=1)) return 0;
									Sa=Sa-1;
									Sb=Sb+Eb+Mb+1;
									Eb=0;
									Mb=0;
								}
								else
								{
									if (unknown_int())
									{
										if (! (I>=1)) return 0;
										I=I-1;
										Sb=Sb+Eb+Mb+1;
										Eb=0;
										Mb=0;
									}
									else
									{
										if (unknown_int())
										{
											if (! (I >= 1)) return 0;
											I = I -1;
											Sa = Sa + Ea + Ma + 1;
											Ea = 0;
											Ma =0;
										}
										else
										{
											if (! (Sb >= 1)) return 0;
											Sb = Sb-1;
											Sa = Ea+Ma+1;
											Ea = 0;
											Ma = 0;

										}
									}
								}
							}
						}
					}
				}
			}
		}
	}

	{;//@ assert(Ea + Ma <= 1);
}
	{;//@ assert(Eb + Mb <= 1);
}
