}
	{;//@ assert(Ea  >= 0);
}
	{;//@ assert(Sb >= 0);
}
	{;//@ assert(Mb  >= 0);
}
	{;//@ assert(Eb  >= 0);
}
	{;//@ assert(I + Sa + Ea + Ma + Sb + Eb + Mb >= 1);
}
