										I=I+Sb+Eb+Mb;
										Sb=0;
										Eb=1;
										Mb=0;
									}
									else
									{
										if (unknown_int())
										{
											if (! (Ea >=1)) return 0;
											Ea = Ea -1;
											Ma = Ma +1;
										}
										else
										{
											if (! (Eb >=1)) return 0;
											Eb = Eb -1;
											Mb = Mb +1;
										}
									}
								}
							}
						}
					}
				}
			}
		}
	}

	{;//@ assert(Ea + Ma <= 1);
}
	{;//@ assert(Eb + Mb <= 1);
}
	{;//@ assert(Mb  >= 0);
}
	{;//@ assert(Eb  >= 0);
}
	{;//@ assert(Ma  >= 0);
}
	{;//@ assert(Ea  >= 0);
}
}
