int flag = 0;
int read( int loc , int len )
{
    int count = 0;
    if( flag == 1 ) 
        return 0;
    while( loc < len )
    {
        int num =  unknown_int();
        if( num == 0 ) 
        {
            return -1;
        }
        else
        {
            if( num < 0 )
                num =  -num;
            num = num % 1000;
            count++;
            if( num < 995 ) 
