    if( size <= 0 || size > 65536 )
        return 0;
    while( pos < size )
    {
        int rc = read( pos, size - pos);
        if( rc == -1 )
        {
            errno++;
            if( errno == 5 )
                return 0;
            continue;
        }
        if( rc == 0 )
            rc++;
        pos += rc;
    }
    return 0;
}

