  int low = unknown_int();
  int mid = unknown_int();
  int high = unknown_int();
  if (!(low == 0 && mid >= 1 && high == 2*mid)) return 0;
  /*@
loop invariant mid >= 0;
loop invariant low + mid * 2 == high;
loop invariant low == 0 && mid >= 1 && high == 2 * mid;
loop invariant high == 2 * mid + low;
