#define assume(e) if(!(e)) return 0;

int main(){

  int x = 0;
  int y = 0;
  int N;

  if(N < 0)
    return 1;

  /*@
loop invariant 0 <= x;
loop invariant 0 <= y;
loop invariant x <= N + 1;
loop invariant x == y || x == y + N + 1;
loop invariant (y >= 0) ==> (x <= N);
loop invariant x <= N+2 ==> y >= 0;
loop invariant x >= N+1 ==> y <= x - N - 2;
loop invariant x >= 0;
loop invariant y >= 0;
loop invariant x <= N ==> y == x;
loop invariant x >= N + 1 ==> y == 2 * N + 1 - x;
loop invariant x >= N + 1 ==> y == 2 * N + 2 - x;
loop invariant (x <= N) ==> (y >= 0);
loop invariant (x >= N+1) ==> (y <= x - (N+1));
loop invariant y == x - (N+1) || y == x;
*/
while (1){
     if (x <= N)
        y++;
     else if(x >= N+1)
       y--;
     else return 1;

     if ( y < 0)
       break;
     x++;
  }

  if(N >= 0)
    if(y == -1)
      if (x >= 2 * N + 3)
        goto ERROR;

  return 1;
