loop invariant (x < 0) ==> y > 0;
loop invariant x + y * (y - 1) / 2 < 0 ==> x < 0;
*/
while (x < 0) {
	x = x + y;
	y++;
    }
    {;//@ assert(y > 0);
}
    return 0;
