{
    Node* menu =(Node*)malloc(sizeof(Node));
    menu->selected = unknown_int();
    menu->size = unknown_int();
    if( menu->selected <= -2 || menu->size < 1 )
        return 0;
    int pos = menu->selected;
    int direction;
    int action_id =  unknown_int();
    if( action_id > 0 && pos >= 1 )
    {
        pos--;
        direction = -1;
    }
    else
    {
        pos++;
        direction = 1;
