void main() {
  int k = 100;
  int b = 0;
  int i;
  int j;
  int n;
  i = j = 0;
  /*@
loop invariant 0 <= n <= 2 * k;
loop invariant i + j == n;
loop invariant i == j || i == j + 1;
loop invariant b == (n % 2 == 0);
loop invariant 0 <= n <= 2*k;
loop invariant b == (n % 2 == 1);
loop invariant b == n % 2;
loop invariant n == i + j;
loop invariant (b == 0 && i == j) || (b == 1 && i == j-1);
loop invariant (b == 0 && i == j) || (b == 1 && i + 1 == j);
*/
for( n = 0 ; n < 2*k ; n++ ) {

    if(b) {
      i++;
    } else {
      j++;
    }
    b = !b;
  }
  {;//@ assert(i == j);
}
}
```

